{
  "status": "OK",
  "results": {
    "recipe_id": 24099,
    "name": "Classic Green Bean Casserole",
    "thumbimg": "http://www.cscassets.com/recipes/small_116x80/small_24099.jpg",
    "bigimg": "http://www.cscassets.com/recipes/xlarge_466x320/xlarge_24099.jpg",
    "description": "It's the dish everyone's expecting on the holidays, but it's so easy to make, you can serve it any day.&#160;What makes our green bean casserole so good?&#160; A&#160;secret ingredient - cream of mushroom soup.",
    "rating": 4.1324,
    "reviewers": 204,
    "recipelink": "/recipedetail.aspx?recipeid=24099",
    "mobileimg": "http://www.cscassets.com/recipes/xlarge_466x320/xlarge_24099.jpg",
    "mobilehdimg": "http://www.cscassets.com/recipes/xlarge_466x320/xlarge_24099.jpg",
    "madewith": [
      "Campbell's&#174; Condensed Cream of Mushroom Soup"
    ],
    "ingredients": [
      {
        "ingredient": "1  can (10 3/4 ounces) Campbell's&#174; Condensed Cream of Mushroom Soup (Regular, 98% Fat Free <strong>or</strong> Healthy Request&#174;)"
      },
      {
        "ingredient": "cup  Milk"
      },
      {
        "ingredient": "1  tsp.  soy sauce"
      },
      {
        "ingredient": "Dash ground black pepper"
      },
      {
        "ingredient": "4  cups cooked cut green beans"
      },
      {
        "ingredient": "1  cup  French's&#174; French Fried Onions"
      },
      {
        "ingredient": ""
      }
    ],
    "steps": [
      {
        "number": 1,
        "step": "Mix soup, milk, soy, black pepper, beans and <b>2/3 cup</b> onions in 1 1/2-qt. casserole."
      },
      {
        "number": 2,
        "step": "Bake at 350F. for 25 min. or until hot."
      },
      {
        "number": 3,
        "step": "Stir . Sprinkle with remaining onions. Bake 5 min."
      },
      {
        "number": 4,
        "step": "Tip: Use <b>1 bag</b> (16 to 20 oz.) frozen green beans, <b>2 pkg.</b> (9 oz. <b>each</b>) frozen green beans, <b>2 cans</b> (about 16 oz. <b>each</b>) green beans <b>or</b> about <b>1 1/2 lb.</b> fresh green beans for this recipe."
      },
      {
        "number": 5,
        "step": "Tip: For a change of pace, substitute <b>4 cups</b> cooked broccoli flowerets for the green beans."
      },
      {
        "number": 6,
        "step": "Tip: For a creative twist, stir in <b>1/2 cup</b> shredded Cheddar cheese with soup. Omit soy sauce. Sprinkle with <b>1/4 cup</b> additional Cheddar cheese when adding the remaining onions.\r\n"
      },
      {
        "number": 7,
        "step": "Tip: For a festive touch, stir in <b>1/4 cup</b> chopped red pepper with soup."
      },
      {
        "number": 8,
        "step": "Tip: For a heartier mushroom flavor, substitute Campbell's Condensed Golden Mushroom Soup for Cream of Mushroom Soup. Omit soy sauce. Stir in <b>1/4 cup</b> chopped red pepper with green beans."
      }
    ],
    "nutrition": {
      "recipe_id": "24099",
      "calories": "454",
      "totalfat": "25g",
      "saturatedfat": "6g",
      "cholesterol": "11mg",
      "sodium": "1243mg",
      "totalcarbohydrate": "51g",
      "dietaryfiber": "9g",
      "protein": "9g",
      "vitamina": "36%DV",
      "vitaminc": "42%DV",
      "calcium": "23%DV",
      "iron": "9%DV"
    },
    "glance": {
      "source": "Campbell's Kitchen",
      "prep": "10 mins",
      "bake": "30 mins",
      "make": "6 servings (about 3/4 cup each)"
    }
  }
}

